class AbstractWrapper {
  /**
   * AbstractWrapper is the parent class for abstract classes that interact with the
   * store and the contract wrappers. The purpose of these classes are to separate the
   * metadata storage and retrieval logic from the on chain contracts.
   * @param {object} storeProvider store provider object
   * @param {object} arbitratorWrapper arbitrator contract wrapper object
   * @param {object} arbitrableWrapper arbitrable contract wrapper object
   */
  constructor(storeProvider, arbitratorWrapper, arbitrableWrapper) {
    this._StoreProvider = storeProvider
    this._Arbitrator = arbitratorWrapper
    this._ArbitrableContract = arbitrableWrapper
  }

  /**
  * set store wrapper
  * @param {object} storeWrapper wrapper for store
  */
  setStore = storeWrapper => {
    this._StoreProvider = storeWrapper
  }

  /**
  * set Arbitrator wrapper
  * @param {object} arbitratorWrapper wrapper for arbitrator contract
  */
  setArbitrator = arbitratorWrapper => {
    this._Arbitrator = arbitratorWrapper
  }

  /**
  * set Arbitrable wrapper
  * @param {object} arbitrableWrapper wrapper for arbitrable contract
  */
  setArbitrable = arbitrableWrapper => {
    this._ArbitrableContract = arbitrableWrapper
  }

  /**
  * I can't wait for decorators
  * throws an error if Arbitrator and Arbitable contract wrappers are not set yet
  */
  _checkArbitratorWrappersSet = () => {
    if (!this._Arbitrator) throw new Error('No Arbitrator Contract Wrapper specified. Please call setArbitrator')
  }

  /**
  * I can't wait for decorators
  * throws an error if Arbitrator and Arbitable contract wrappers are not set yet
  */
  _checkArbitrableWrappersSet = () => {
    if (!this._ArbitrableContract) throw new Error('No Arbitrable Contract Wrapper specified. Please call setArbitrable')
  }

  /**
  * Load instance of arbitrator contract
  * @param {string} arbitratorAddress address
  * @return instance of arbitrator contract wrapper
  */
  _loadArbitratorInstance = async arbitratorAddress => {
    this._checkArbitratorWrappersSet()
    return await this._Arbitrator.load(arbitratorAddress)
  }

  /**
  * Load instance of arbitrable contract
  * @param {string} arbitrableAddress address
  * @return instance of arbitrable contract wrapper
  */
  _loadArbitrableInstance = async arbitrableAddress => {
    this._checkArbitrableWrappersSet()
    return await this._ArbitrableContract.load(arbitrableAddress)
  }
}

export default AbstractWrapper

import AbstractWrapper from './AbstractWrapper'
import {
  DEFAULT_ARBITRATION_COST,
  DISPUTE_STATUS
} from '../../constants'

/**
 * Arbitrable Contract api
 */
class ArbitrableContract extends AbstractWrapper {
  /**
   * Arbitrable Contract Constructor
   * @param {object} storeProvider store provider object
   * @param {object} arbitrableWrapper arbitrable contract wrapper object
   */
  constructor(storeProvider, arbitrableWrapper) {
    super(storeProvider, undefined, arbitrableWrapper)
  }

  /**
  * Deploy a contract and add to the Store
  * @param {string} account Ethereum address
  * @param {int} value funds to be placed in contract
  * @param {string} hashContract Keccak hash of the plain English contract
  * @param {string} arbitratorAddress The address of the arbitrator contract
  * @param {int} timeout Time after which a party automatically loose a dispute
  * @param {string} partyB Ethereum address of the other party in the contract
  * @param {bytes} arbitratorExtraData Extra data for the arbitrator
  * @param {string} email Email address of the contract creator (default empty string)
  * @param {string} description Description of what the contract is about (default empty string)
  * @param args Extra arguments for the contract
  * @return object | Error
  */
  deployContract = async (
    account,
    value,
    hashContract,
    arbitratorAddress,
    timeout,
    partyB,
    arbitratorExtraData = '',
    email = '',
    description = '',
    ...args
  ) => {
    this._checkArbitrableWrappersSet()

    const contractInstance = await this._ArbitrableContract.deploy(
      account,
      value,
      hashContract,
      arbitratorAddress,
      timeout,
      partyB,
      arbitratorExtraData,
      ...args
    )

    await this._StoreProvider.updateContract(
      contractInstance.address,
      hashContract,
      account,
      partyB,
      arbitratorAddress,
      timeout,
      email,
      description
    )

    // return contract data
    return await this.getData(contractInstance.address, account)
  }

  /**
   * Submit evidence
   * @param {string} account ETH address of user
   * @param {string} contractAddress ETH address of contract
   * @param {string} name name of evidence
   * @param {string} description description of evidence
   * @param {string} evidence A link to an evidence using its URI.
   * @return {string} txHash Hash transaction
   */
  submitEvidence = async (
    account,
    contractAddress,
    name,
    description = '',
    url
  ) => {
    const txHash = await this._ArbitrableContract.submitEvidence(
      account,
      contractAddress,
      name,
      description,
      url
    )

    await this._StoreProvider.addEvidenceContract(
      contractAddress,
      account,
      name,
      description,
      url
    )

    return txHash
  }

  /**
  * Get ruling options from dispute via event
  * @param {string} contractAddress
  * @returns {object[]} an array of objects that specify the name and value of the resolution option
  */
  getRulingOptions = async (
    contractAddress
  )  => {
    const contractInstance = await this._loadArbitrableInstance(contractAddress)

    // fetch dispute resolution options
    const statusNumber = (await contractInstance.status()).toNumber()

    // should this just be !== ?
    if (statusNumber < DISPUTE_STATUS) return []

    // FIXME we should have a block number to start from so we don't have to rip through the entire chain
    const disputeEvent = await new Promise((resolve, reject) => {
      contractInstance.Dispute({}, {fromBlock: 0, toBlock: 'latest'}).get((error, eventResult) => {
        if (error) reject(error)
        // this should be ok because there should only be 1 Dispute event per contract
        resolve(eventResult[0])
      })
    })

    if (!disputeEvent) return []

    // FIXME there should only be one create dispute event per contract for now. allow abstract number
    const rulingOptions = disputeEvent.args._rulingOptions.split(';')
    let optionIndex = 0
    const resolutionOptions = rulingOptions.map(option => {
      optionIndex += 1
      return {
        name: option,
        value: optionIndex
      }
    })

    return resolutionOptions
  }

  getArbitrator = async (
    arbitrableContractAddress
  ) => {
    const contractInstance = await this._loadArbitrableInstance(arbitrableContractAddress)

    return await contractInstance.arbitrator()
  }

  /**
  * Get data from the store and contract for Arbitrable Contract
  * @param {string} contractAddress address of Arbitrable Contract
  * @param {string} account ETH address of user
  * @return {object} contract data
  */
  getData = async (
    contractAddress,
    account
  ) => {
    const contractData = await this._ArbitrableContract.getData(contractAddress)

    let storeData = {}
    if (account) storeData = await this._StoreProvider.getContractByAddress(account, contractAddress)

    return Object.assign({}, storeData, contractData)
  }
}

export default ArbitrableContract

import AbstractWrapper from './AbstractWrapper'
import _ from 'lodash'

/**
 * Arbitrator api
 */
class Arbitrator extends AbstractWrapper {
  /**
   * Arbitrator Constructor
   * @param {object} storeProvider store provider object
   * @param {object} arbitratorWrapper arbitrator contract wrapper object
   */
  constructor(storeProvider, arbitratorWrapper) {
    super(storeProvider, arbitratorWrapper, undefined)
  }

  // passthroughs
  getPNKBalance = this._Arbitrator.getPNKBalance
  activatePNK = this._Arbitrator.activatePNK
  getData = this._Arbitrator.getData
  passPeriod = this._Arbitrator.passPeriod

  /**
   * @param {number} amount number of pinakion to buy
   * @param {address} account address of user
   * @return {object[]} balance of user
   */
  buyPNK = async (
    amount,
    arbitratorAddress, // address of KlerosPOC
    account
  ) => {
    const txHash = await this._Arbitrator.buyPNK(amount, arbitratorAddress, account)
    if (txHash) {
      // update store so user can get instantaneous feedback
      let userProfile = await this._StoreProvider.getUserProfile(account)
      if (_.isNull(userProfile)) userProfile = await this._StoreProvider.newUserProfile(account)
      // FIXME seems like a super hacky way to update store
      userProfile.balance = (parseInt(userProfile.balance) ? userProfile.balance : 0) + parseInt(amount)
      delete userProfile._id
      delete userProfile.created_at
      const response = await this._StoreProvider.newUserProfile(account, userProfile)

      return this.getPNKBalance(arbitratorAddress, account)
    } else {
      throw new Error("unable to buy PNK")
    }
  }

  /**
   * Get all contracts TODO do we need to get contract data from blockchain?
   * @param {string} account address of user
   * @return {object[]} contract data from store
   */
  getContractsForUser = async (
    account
  ) => {
    // fetch user profile
    let userProfile = await this._StoreProvider.getUserProfile(account)
    if (_.isNull(userProfile)) userProfile = await this._StoreProvider.newUserProfile(account)

    return userProfile.contracts
  }
}

export default Arbitrator

import AbstractWrapper from './AbstractWrapper'
import {
  NULL_ADDRESS,
  VOTING_PERIOD,
  DEFAULT_ARBITRATION_COST,
  DISPUTE_STATUS
} from '../../constants'
import _ from 'lodash'

/**
 * Disputes api
 */
class Disputes extends AbstractWrapper {
  /**
   * Disputes Constructor
   * @param {object} storeProvider store provider object
   * @param {object} arbitratorWrapper arbitrator contract wrapper object
   * @param {object} arbitrableWrapper arbitrable contract wrapper object
   */
  constructor(storeProvider, arbitratorWrapper, arbitrableWrapper) {
    super(storeProvider, arbitratorWrapper, arbitrableWrapper)
    this._disputeWatchers = {}
    this.disputeWatcher
  }

  /**
  * If there is a dispute in contract update store
  * FIXME contracts with multiple disputes will need a way to clarify that this is a new dispute
  * @param {string} arbitratorAddress
  */
  watchForDisputes = async (
    arbitratorAddress,
  ) => {
    this._checkArbitratorWrappersSet()

    const lastBlock = await this._StoreProvider.getLastBlock(arbitratorAddress)
    const currentBlock = this._Arbitrator._getCurrentBlockNumber()
    const contractInstance = await this._loadArbitratorInstance(arbitratorAddress)
    this.disputeWatcher = contractInstance.DisputeCreation({}, {fromBlock: lastBlock, toBlock: 'latest'})

    if (lastBlock < currentBlock) {
      // FETCH DISPUTES WE MIGHT HAVE MISSED
      this.disputeWatcher.get((error, eventResult) => {
        if (!error) {
          eventResult.map(event => {
            // add new dispute to store
            this._updateStoreForDispute(arbitratorAddress, event.args._disputeID)
          })

          this._StoreProvider.updateLastBlock(arbitratorAddress, currentBlock)
        }
      })
    }

    // WATCH FOR NEW DISPUTES
    this.disputeWatcher.watch((error, result) => {
      if (!error) {
        const disputeId = result.args._disputeID
        this._updateStoreForDispute(arbitratorAddress, disputeId)

        this._StoreProvider.updateLastBlock(arbitratorAddress, result.blockNumber)
      }
    })
  }

  stopWatchingForDisputes = () => {
    this.disputeWatcher.stopWatching()
  }

  /**
  * Pay the arbitration fee to raise a dispute. To be called by the party A.
  * @param {string} account Ethereum account
  * @param {string} arbitrableContract Address address of arbitrable contract
  * @param {number} arbitrationCost Amount to pay the arbitrator
  * @return {string} txHash hash transaction | Error
  */
  raiseDisputePartyA = async (
    account,
    arbitrableContractAddress,
    arbitrationCost = DEFAULT_ARBITRATION_COST
  ) => {
    this._checkArbitrableWrappersSet()

    try {
      const txHash = await this._ArbitrableContract.payArbitrationFeeByPartyA(
        account,
        arbitrableContractAddress,
        arbitrationCost
      )

      if (!txHash) throw new Error('unable to pay arbitration fee for party A')

      return txHash
    } catch (e) {
      throw new Error(e)
    }
  }

  /**
  * Pay the arbitration fee to raise a dispute. To be called by the party B.
  * @param {string} account Ethereum account
  * @param {string} arbitrableContract Address address of arbitrable contract
  * @param {number} arbitrationCost Amount to pay the arbitrator
  * @return {string} txHash hash of the transaction | Error
  */
  raiseDisputePartyB = async (
    account,
    arbitrableContractAddress,
    arbitrationCost = DEFAULT_ARBITRATION_COST
  ) => {
    this._checkArbitrableWrappersSet()

    const txHash = await this._ArbitrableContract.payArbitrationFeeByPartyB(
      account,
      arbitrableContractAddress,
      arbitrationCost
    )

    if (!txHash) throw new Error('unable to pay arbitration fee for party B')

    return txHash
  }

  /**
   * Get disputes for user with extra data from arbitrated transaction and store
   * @param {string} arbitratorAddress address of Kleros contract
   * @param {string} account address of user
   * @return {object[]} dispute data objects for user
   */
  getDisputesForUser = async (
    arbitratorAddress,
    account
  ) => {
    // FIXME don't like having to call this every fnc
    this._checkArbitratorWrappersSet()
    this._checkArbitrableWrappersSet()
    // contract data
    const arbitratorData = await this._Arbitrator.getData(arbitratorAddress, account)

    // fetch user profile
    let profile = await this._StoreProvider.getUserProfile(account)
    if (_.isNull(profile)) profile = await this._StoreProvider.newUserProfile(account)
    // fetch current contract period
    const period = arbitratorData.period
    const currentSession = arbitratorData.session
    // new jurors have not been chosen yet. don't update
    if (period !== VOTING_PERIOD) {
      let disputes = await this._StoreProvider.getDisputesForUser(account)
      disputes = await Promise.all(disputes.map(async (dispute) => {
        return await this.getDataForDispute(dispute.arbitratorAddress, dispute.disputeId, account)
      }))

      return disputes
    }

    if (currentSession != profile.session) {
      // get disputes for juror
      const myDisputeIds = await this.getDisputesForJuror(arbitratorAddress, account)
      // update store for each dispute
      await Promise.all(myDisputeIds.map(async disputeId => {
        await this._updateStoreForDispute(arbitratorAddress, disputeId, account)
      }))

      // update session on profile
      profile = await this._StoreProvider.getUserProfile(account)
      profile.session = currentSession
      await this._StoreProvider.updateUserProfile(account, profile)
    }
    // return array of all disputes for user
    let disputes = await this._StoreProvider.getDisputesForUser(account)
    disputes = await Promise.all(disputes.map(async (dispute) => {
      return await this.getDataForDispute(dispute.arbitratorAddress, dispute.disputeId, account)
    }))

    return disputes
  }

  /**
   * Get disputes from Kleros contract
   * @param {string} arbitratorAddress address of Kleros contract
   * @param {string} account address of user
   * @return {int[]} array of dispute id's
   */
  getDisputesForJuror = async (
    arbitratorAddress,
    account,
  ) => {
    // FIXME don't like having to call this every fnc
    this._checkArbitratorWrappersSet()
    // contract data
    const arbitratorData = await this._Arbitrator.getData(arbitratorAddress, account)
    const myDisputes = []
    let disputeId = 0
    const currentSession = arbitratorData.session

    let dispute
    while (1) {
      // iterate over all disputes (FIXME inefficient)
      try {
         dispute = await this._Arbitrator.getDispute(arbitratorAddress, disputeId)
         if (dispute.arbitratedContract === NULL_ADDRESS) break
         // session + number of appeals
         const disputeSession = dispute.firstSession + dispute.numberOfAppeals
         // if dispute not in current session skip
         if (disputeSession !== currentSession) {
           disputeId++
           dispute = await this._Arbitrator.getDispute(arbitratorAddress, disputeId)
           continue
         }

         const votes = await this.getVotesForJuror(arbitratorAddress, disputeId, account)
         if (votes.length > 0) {
           myDisputes.push(
             disputeId
           )
         }
         // check next dispute
         disputeId += 1
      } catch (e) {
        // getDispute(n) throws an error if index out of range
        break
      }
    }

    return myDisputes
  }

  /**
  * Fetch the votes a juror has in a dispute
  * @param {string} arbitratorAddress address of the arbitrator contract
  * @param {number} disputeId id of the dispute
  * @param {string} account potential jurors address
  * @return {number[]} array of integers indicating the draw
  */
  getVotesForJuror = async (
    arbitratorAddress,
    disputeId,
    account
  ) => {
    const numberOfJurors = await this._Arbitrator.getAmountOfJurorsForDispute(arbitratorAddress, disputeId)
    const votes = []
    // FIXME map doesn't seem to make sense here. would need to construct array of possible choices and then filter?
    for (let draw=1; draw<=numberOfJurors; draw++) {
      const isJuror = await this._Arbitrator.isJurorDrawnForDispute(disputeId, draw, arbitratorAddress, account)
      if (isJuror) {
        votes.push(draw)
      }
    }

    return votes
  }

  /**
   * Submit votes. Note can only be called during Voting period (Period 2)
   * @param {string} arbitratorAddress address of KlerosPOC contract
   * @param {number} disputeId index of the dispute
   * @param {number} ruling int representing the jurors decision
   * @param {number[]} votes int[] of drawn votes for dispute
   * @param {string} account address of user
   * @return {string} transaction hash | Error
   */
  submitVotesForDispute = async (
    arbitratorAddress,
    disputeId,
    ruling,
    votes,
    account
  ) => {
    const txHash = await this._Arbitrator.submitVotes(
      arbitratorAddress,
      disputeId,
      ruling,
      votes,
      account
    )

    if (txHash) {
      // FIXME don't like having to fetch data just to get the arbitratedContract
      const disputeData = await this._Arbitrator.getDispute(arbitratorAddress, disputeId)
      // mark in store that you have ruled on dispute
      await this._StoreProvider.updateDisputeProfile(
        account,
        votes,
        arbitratorAddress,
        disputeId,
        true,
        true
      )

      return txHash
    } else {
      throw new Error('unable to submit votes')
    }
  }

  /**
  * get the deadline for dispute
  * @param {string} arbitratorAddress address of arbitrator contract
  * @param {number} period default to voting period
  * @return {string} date string in the form dd/mm/yyyy
  */
  getDeadlineForDispute = async (
    arbitratorAddress,
    period = VOTING_PERIOD
  ) => {
    const arbitratorData = await this._Arbitrator.getData(arbitratorAddress)
    // compute end date
    const startTime = arbitratorData.lastPeriodChange
    const length = await this._Arbitrator.getTimeForPeriod(arbitratorAddress, period)
    // FIXME this is all UTC for now. Timezones are a pain
    const deadline = new Date(0);
    deadline.setUTCSeconds(startTime)
    deadline.setSeconds(deadline.getSeconds() + length);

    return `${deadline.getUTCDate()}/${deadline.getUTCMonth()}/${deadline.getFullYear()}`
  }

  /**
  * update store with new dispute data
  * @param {string} arbitratorAddress Address address of arbitrator contract
  * @param {int} disputeId index of dispute
  * @param {string} jurorAddress <optional> address of juror
  */
  _updateStoreForDispute = async (
    arbitratorAddress,
    disputeId,
    jurorAddress
  ) => {
    const disputeData = await this.getDataForDispute(
      arbitratorAddress,
      disputeId,
      jurorAddress
    )

    // update dispute
    await this._StoreProvider.updateDispute(
      disputeData.disputeId,
      disputeData.arbitratorAddress,
      disputeData.hash,
      disputeData.partyA,
      disputeData.partyB,
      disputeData.title,
      disputeData.deadline,
      disputeData.status,
      disputeData.fee,
      disputeData.information,
      disputeData.justification,
      disputeData.resolutionOptions
    )

    // update profile partyA
    await this._StoreProvider.updateDisputeProfile(
      disputeData.partyA,
      [],
      disputeData.arbitratorAddress,
      disputeData.disputeId,
      false,
      false
    )

    // update profile partyB
    await this._StoreProvider.updateDisputeProfile(
      disputeData.partyB,
      [],
      disputeData.arbitratorAddress,
      disputeData.disputeId,
      false,
      false
    )

    if (jurorAddress) {
      // update juror profile <optional>
      await this._StoreProvider.updateDisputeProfile(
        jurorAddress,
        disputeData.votes,
        disputeData.arbitratorAddress,
        disputeData.disputeId,
        disputeData.votes.length > 0 ? true : false,
        false
      )
    }
  }

  /**
  * get user data for a dispute from the store
  * @param {string} arbitratorAddress address for arbitrator contract
  * @param {int} disputeId index of dispute
  * @param {string} account jurors address
  * @return {object} dispute data from store for user
  */
  getUserDisputeFromStore = async (
    arbitratorAddress,
    disputeId,
    account
  ) => {
    const userProfile = await this._StoreProvider.getUserProfile(account)

    const disputeArray = _.filter(userProfile.disputes, (dispute) => {
      return (dispute.disputeId === disputeId && dispute.arbitratorAddress == arbitratorAddress)
    })

    if (_.isEmpty(disputeArray)) throw new Error(`User ${account} does not have store data for dispute`)

    return disputeArray[0]
  }

  /**
  * get evidence for contract
  * @param {string} arbitrableContract Address address for arbitrable contract
  * @param {string} account <optional> jurors address
  * @return {object[]} array of evidence objects
  */
  getEvidenceForArbitrableContract = async (
    arbitrableContractAddress
  ) => {
    this._checkArbitrableWrappersSet()

    const arbitrableContractData = await this._ArbitrableContract.getData(arbitrableContractAddress)
    const partyAContractData = await this._StoreProvider.getContractByAddress(
      arbitrableContractData.partyA,
      arbitrableContractAddress
    )
    const partyBContractData = await this._StoreProvider.getContractByAddress(
      arbitrableContractData.partyB,
      arbitrableContractAddress
    )

    const partyAEvidence = partyAContractData ? partyAContractData.evidences : []
    const partyBEvidence = partyBContractData ? partyBContractData.evidences : []

    return partyAEvidence.concat(partyBEvidence)
  }

  /**
  * get data for a dispute
  * @param {string} arbitratorAddress Address address for arbitrator contract
  * @param {int} disputeId index of dispute
  * @param {string} account <optional> jurors address
  * @return {Object} data object for dispute that uses data from the contract and store
  */
  getDataForDispute = async (
    arbitratorAddress,
    disputeId,
    account
  ) => {
    this._checkArbitratorWrappersSet()
    this._checkArbitrableWrappersSet()

    // FIXME should we just return what we have in the store?
    const dispute = await this._Arbitrator.getDispute(arbitratorAddress, disputeId)
    if (!dispute) {
      throw new Error(`Dispute with arbitrator: ${arbitratorAddress} and disputeId: ${disputeId} does not exist`)
    }
    const arbitrableContractAddress = dispute.arbitratedContract

    const arbitrableContractData = await this._ArbitrableContract.getData(arbitrableContractAddress)
    const constractStoreData = await this._StoreProvider.getContractByAddress(
      arbitrableContractData.partyA,
      arbitrableContractAddress
    )

    let votes = []
    let isJuror = false
    let hasRuled = false
    if (account) {
      votes = await this.getVotesForJuror(arbitratorAddress, disputeId, account)
      try {
        const userData = await this.getUserDisputeFromStore(arbitratorAddress, disputeId, account)
        isJuror = userData.isJuror
        hasRuled = userData.hasRuled
      } catch (e) {
        isJuror = false
        hasRuled = false
      }
    }

    // get evidence
    const evidence = await this.getEvidenceForArbitrableContract(arbitrableContractAddress)

    // get deadline
    const deadline = await this.getDeadlineForDispute(arbitratorAddress)

    // get ruling
    const ruling = await this._Arbitrator.currentRulingForDispute(arbitratorAddress, disputeId)

    return ({
      // FIXME hash not being stored in contract atm
      hash: arbitrableContractAddress,
      partyA: arbitrableContractData.partyA,
      partyB: arbitrableContractData.partyB,
      arbitrableContractStatus: arbitrableContractData.status,
      disputeState: dispute.state,
      arbitrableContractAddress: arbitrableContractAddress,
      arbitratorAddress: arbitratorAddress,
      fee: dispute.arbitrationFeePerJuror,
      disputeId: disputeId,
      session: dispute.firstSession + dispute.numberOfAppeals,
      deadline: deadline,
      // store data
      description: constractStoreData ? constractStoreData.description : undefined,
      email: constractStoreData ? constractStoreData.email : undefined,
      votes: votes,
      isJuror: isJuror,
      hasRuled: hasRuled,
      ruling: ruling,
      evidence: evidence
    })
  }
}

export default Disputes

import * as _ from 'lodash'
import BigNumber from 'bignumber'
import contract from 'truffle-contract'
import ContractWrapper from './ContractWrapper'
import KlerosWrapper from './KlerosWrapper'
import arbitrableTransaction from 'kleros-interaction/build/contracts/ArbitrableTransaction'
import config from '../../config'
import { DISPUTE_STATUS } from '../../constants'

/**
 * ArbitrableTransaction API
 */
class ArbitrableTransactionWrapper extends ContractWrapper {
  /**
   * Constructor ArbitrableTransaction.
   * @param {object} web3 instance
   * @param {string} address of the contract (optional)
   */
  constructor(web3Provider, address) {
    super(web3Provider)
    if (!_.isUndefined(address)) {
      this.address = address
    }
    this.contractInstance = null
  }

  /**
   * Deploy ArbitrableTransaction.
   * @param {object} account Ethereum account (default account[0])
   * @param {number} value funds to be placed in contract
   * @param {string} hashContract Keccak hash of the plain English contract. (default null hashed)
   * @param {string} arbitratorAddress The address of the arbitrator contract
   * @param {number} timeout Time after which a party automatically loose a dispute. (default 3600)
   * @param {string} partyB The recipient of the transaction. (default account[1])
   * @param {bytes} arbitratorExtraData Extra data for the arbitrator. (default empty string)
   * @return {object} truffle-contract Object | err The deployed contract or an error
   */
  deploy = async (
      account = this._Web3Wrapper.getAccount(0),
      value = config.VALUE,
      hashContract = 0x6aa0bb2779ab006be0739900654a89f1f8a2d7373ed38490a7cbab9c9392e1ff,
      arbitratorAddress,
      timeout = 100,
      partyB = this._Web3Wrapper.getAccount(1),
      arbitratorExtraData = '',
    ) => {

    const contractDeployed = await this._deployAsync(
      account,
      value,
      arbitrableTransaction,
      arbitratorAddress,
      hashContract,
      timeout,
      partyB,
      arbitratorExtraData
    )

    this.address = contractDeployed.address
    this.contractInstance = contractDeployed

    return contractDeployed
  }

  /**
   * Load an existing arbitrableTransaction contract
   * @param {string} address Contract address
   * @return {object} contractInstance | Error
   */
  load = async address => {
    // return contract instance if already loaded
    if (this.contractInstance && this.contractInstance.address === address) return this.contractInstance

    try {
      const contractInstance = await this._instantiateContractIfExistsAsync(
        arbitrableTransaction,
        address
      )

      this.contractInstance = contractInstance
      this.address = address

      return contractInstance
    } catch (e) {
      throw new Error(e)
    }
  }

  /**
   * Pay the arbitration fee to raise a dispute. To be called by the party A.
   * @param {string} account Ethereum account (default account[1])
   * @param {number} arbitrationCost Amount to pay the arbitrator. (default 10000 wei)
   * @return {string} txHash hash transaction | Error
   */
   payArbitrationFeeByPartyA = async (
     account = this._Web3Wrapper.getAccount(0),
     contractAddress, // ethereum address of the contract
     arbitrationCost = 0.15,
   ) => {
     try {
       this.contractInstance = await this.load(contractAddress)
       const txHashObj = await this.contractInstance
         .payArbitrationFeeByPartyA(
         {
           from: account,
           gas: config.GAS,
           value: this._Web3Wrapper.toWei(arbitrationCost, 'ether'),
         }
       )

       return txHashObj.tx
     } catch (e) {
       throw new Error(e)
     }
   }

  /**
   * Pay the arbitration fee to raise a dispute. To be called by the party B.
   * @param {string} account Ethereum account (default account[1])
   * @param {number} arbitrationCost Amount to pay the arbitrator. (default 10000 wei)
   * @return {string} txHash hash transaction | Error
   */
   payArbitrationFeeByPartyB = async (
     account = this._Web3Wrapper.getAccount(1),
     contractAddress, // ethereum address of the contract
     arbitrationCost = 0.15
   ) => {
     try {
       this.contractInstance = await this.load(contractAddress)
       const txHashObj = await this.contractInstance
         .payArbitrationFeeByPartyB(
         {
           from: account,
           gas: config.GAS,
           value: this._Web3Wrapper.toWei(arbitrationCost, 'ether')
         }
       )

       return txHashObj.tx
     } catch (e) {
       throw new Error(e)
     }
   }

  /**
  * Submit evidence
  * @param {string} account ETH address of user
  * @param {string} contractAddress ETH address of contract
  * @param {string} name name of evidence
  * @param {string} description description of evidence
  * @param {string} evidence A link to an evidence using its URI.
  * @return {string} txHash Hash transaction
   */
  submitEvidence = async (
    account = this._Web3Wrapper.getAccount(0),
    contractAddress,
    name,
    description = '',
    url
  ) => {
    this.contractInstance = await this.load(contractAddress)
    const txHashObj = await this.contractInstance
      .submitEvidence(
        JSON.stringify(
          name,
          description,
          url
        ),
        {
          from: account,
          gas: config.GAS,
          value: 0
        }
      )

    return txHashObj.tx
  }

  /**
  * Data of the contract
  * @param {string} account Address of the party.
  * @param {string} address Address of the ArbitrableTransaction contract.
  * @return {object} Object Data of the contract.
  */
  getData = async (
    address
  ) => {
    const contractInstance = await this.load(address)

    const [
      arbitrator,
      // hashContract, // FIXME getter for the hash contract see contractHash see https://github.com/kleros/kleros-interaction/blob/master/test/TwoPartyArbitrable.js#L19
      extraData,
      timeout,
      partyA,
      partyB,
      status,
      arbitratorExtraData,
      disputeId,
      partyAFee,
      partyBFee
      ] = await Promise.all(
        [
          contractInstance.arbitrator.call(),
          contractInstance.arbitratorExtraData.call(),
          //  contractInstance.hashContract.call(),
          contractInstance.timeout.call(),
          contractInstance.partyA.call(),
          contractInstance.partyB.call(),
          contractInstance.status.call(),
          contractInstance.arbitratorExtraData.call(),
          contractInstance.disputeID.call(),
          contractInstance.partyAFee.call(),
          contractInstance.partyBFee.call(),
        ]
      ).catch(err => {
        throw new Error(err)
      })

    return {
      address,
      arbitrator,
      extraData,
      address,
      timeout: timeout.toNumber(),
      partyA,
      partyB,
      status: status.toNumber(),
      arbitratorExtraData,
      disputeId: disputeId.toNumber(),
      partyAFee: partyAFee.toNumber(),
      partyBFee: partyBFee.toNumber(),
    }
  }
}

